function ValidateEmail(e){return e.value.match(/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/)?(document.getElementById("invalidEmailLable").innerHTML="",document.getElementById("emailLabel").innerHTML="Email",!0):(document.getElementById("invalidEmailLable").innerHTML="Please add a valid email address",document.getElementById("emailLabel").innerHTML="",document.getElementById("emailFormGroup").classList.add("invalid"),!1)}